mapper.select:
SELECT COUNT(USERID) FROM MEMBER
WHERE USERID LIKE #{userid}
AND PASSWORD LIKE #{password}

mapper.update:
UPDATE MEMBER SET 
PASSWORD = #{password},
ROLL = #{roll},
TEAMID = #{teamid}
WHERE USERID LIKE #{userid}

mapper.delete:
DELETE FROM MEMBER 
WHERE USERID LIKE #{userid} 
AND PASSWORD LIKE #{password}
